#Start Transcript
Start-Transcript -Path "C:\PanoptoRecorder\PanoptoCleaner-Install-Log.txt" -Force

#schedule 
$repeat = (New-TimeSpan -Days 1)

$trigger = New-JobTrigger -Once -At (Get-Date).Date.AddHours(12) -RepeatIndefinitely -RepetitionInterval $repeat

$User = "SYSTEM"
$Action = New-ScheduledTaskAction -Execute "powershell.exe" -Argument "-ex bypass -encodedcommand "

Register-ScheduledTask -TaskName "Panopto Cleaner" -Trigger $Trigger -User $User -Action $Action -Force 
Set-ScheduledTask -Taskname "Panopto Cleaner" -Settings $(New-ScheduledTaskSettingsSet -StartWhenAvailable -AllowStartIfOnBatteries -DontStopIfGoingOnBatteries)

$Exists = Get-ScheduledTask -TaskName 'Panopto Cleaner'
if($Exists){
    Write-Host "Successfully Created the Scheduled Task: 'Panopto Cleaner'"}

Stop-Transcript